import logo from './logo.svg';
import './App.css';
import Post from './Post.js';
import React from 'react';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      posts: [{userID: "userID1", id: 1, title: "Title1", body: "body1"}]
    };

    fetch('https://jsonplaceholder.typicode.com/posts').then((response) => response.json()).then((json) => {this.setState({posts: JSON.parse(json)})});
  }

  render() {
    return (
      <div className="body">
      <h1>List of posts</h1>
      {this.state.posts.map((item) => (
        <Post key={item.id} post={item}/>
      ))}
      </div>
    );
  }
}

export default App;
